// Search Nodes Tool - Recherche de nodes n8n
const query = $fromAI('query', 'string', 'Search term for n8n nodes');

if (!query) {
  throw new Error('Query parameter is required');
}

// Retourne des instructions pour l'agent
return {
  instructions: `Pour rechercher des nodes n8n avec le terme "${query}", utilisez la documentation n8n officielle ou consultez https://docs.n8n.io/integrations/`,
  searchTerm: query,
  suggestions: [
    'HTTP Request - Pour les appels API',
    'Webhook - Pour recevoir des données',
    'Code - Pour du JavaScript custom',
    'Set - Pour transformer les données',
    'IF - Pour la logique conditionnelle'
  ]
};

// Create Workflow Tool - Création de workflows n8n
const name = $fromAI('name', 'string', 'Workflow name');
const description = $fromAI('description', 'string', 'Workflow description', '');

if (!name) {
  throw new Error('Workflow name is required');
}

// Retourne un template de workflow
return {
  message: `Template pour créer le workflow "${name}"`,
  workflowTemplate: {
    name: name,
    nodes: [
      {
        name: 'Start',
        type: 'n8n-nodes-base.manualTrigger',
        position: [250, 300],
        parameters: {}
      }
    ],
    connections: {},
    settings: {
      executionOrder: 'v1'
    }
  },
  instructions: 'Utilisez ce template comme base et ajoutez vos nodes spécifiques'
};

// Explain Node Tool - Explications sur les nodes n8n
const nodeType = $fromAI('nodeType', 'string', 'Node type to explain');

if (!nodeType) {
  throw new Error('Node type parameter is required');
}

// Base de connaissances simplifiée
const nodeExplanations = {
  'httpRequest': {
    name: 'HTTP Request',
    description: 'Effectue des requêtes HTTP (GET, POST, PUT, DELETE) vers des APIs',
    commonUses: ['Appeler des APIs REST', 'Récupérer des données externes', 'Envoyer des webhooks'],
    requiredParams: ['url', 'method']
  },
  'webhook': {
    name: 'Webhook',
    description: 'Reçoit des données via HTTP (trigger)',
    commonUses: ['Recevoir des événements externes', 'Créer des APIs', 'Intégrations push'],
    requiredParams: ['path']
  },
  'code': {
    name: 'Code',
    description: 'Exécute du JavaScript ou Python custom',
    commonUses: ['Transformations complexes', 'Logique métier', 'Calculs'],
    requiredParams: ['code']
  },
  'set': {
    name: 'Set',
    description: 'Modifie ou crée des champs de données',
    commonUses: ['Renommer des champs', 'Ajouter des valeurs', 'Nettoyer les données'],
    requiredParams: ['values']
  },
  'if': {
    name: 'IF',
    description: 'Logique conditionnelle (si/sinon)',
    commonUses: ['Router les données', 'Filtrer', 'Logique de décision'],
    requiredParams: ['conditions']
  }
};

const cleanNodeType = nodeType.replace('n8n-nodes-base.', '').toLowerCase();
const explanation = nodeExplanations[cleanNodeType];

if (explanation) {
  return {
    nodeType: nodeType,
    ...explanation,
    documentationUrl: `https://docs.n8n.io/integrations/builtin/core-nodes/n8n-nodes-base.${cleanNodeType}/`
  };
} else {
  return {
    nodeType: nodeType,
    message: `Node "${nodeType}" non trouvé dans la base de connaissances`,
    suggestion: `Consultez https://docs.n8n.io/integrations/ pour plus d'informations`
  };
}

// Récupère tous les items
const items = $input.all();

// Parcourt chaque item
for (let item of items) {
  if (item.json.output) {
    // Décode les échappements JSON
    let text = item.json.output;

    // Remplace tous les \\n par de vrais sauts de ligne
    text = text.replace(/\\n/g, '\n');

    // Remplace aussi les \\t si besoin
    text = text.replace(/\\t/g, '\t');

    // Met à jour la réponse
    item.json.output = text;
  }

  // Si tu utilises "response" au lieu de "output"
  if (item.json.response) {
    item.json.response = item.json.response.replace(/\\n/g, '\n').replace(/\\t/g, '\t');
  }
}

return items;
